# look at the csv in this directory.
# CSV headings:
# name,Address,Pay on mainnet,% Epoch 3,% Epoch 5,% Epoch 6,% Epoch 7,$ Epoch 8,% Epoch 9,% Epoch 10,Total ,Percentage of Budget,BAL award if program ended today
# Generate a payload that adds up teh total for all the rows that do not have yes in Pay on mainnet. Bridge this to gnosis chain
# There should also be a transfer for each mainnet address in the amount specified.
# Then generate a second payload on gnosis chain to pay out the amount bridged over

mainnet_builder = SafeTxBuilder(
    "multisigs/lm", chain_id=str(AddrBook.chain_ids_by_name["mainnet"])
)
BAL_token_mainnet = SafeContract("tokens/BAL", get_abi("ERC20"))
omnibridge_mainnet = SafeContract(GNOSIS_OMNIBRIDGE, BRIDGE_ABI)
send_to_gnosis = 0
gnosis_payouts = {}
# Load the csv
with open("ZenBetaAirdrop.csv") as f:
    reader = csv.DictReader(f)
    for row in reader:
        address = row["Address"]
        amount = Decimal(Decimal(row["Total "]) * Decimal(1e18)).to_integral_value(
            rounding=ROUND_DOWN
        )
        if row["Pay on mainnet"].lower() == "yes":
            BAL_token_mainnet.transfer(address, amount)
        else:
            send_to_gnosis += amount
            gnosis_payouts[address] = amount

BAL_token_mainnet.approve(omnibridge_mainnet.address, send_to_gnosis)
omnibridge_mainnet.relayTokens(
    BAL_token_mainnet.address, AddrBook("gnosis").multisigs.lm, send_to_gnosis
)
mainnet_builder.output_payload("ZenAirdropMainnet.json")

## Invoke new SafeTxBuilder for fresh singleton
gnosis_builder = SafeTxBuilder(
    "multisigs/lm", chain_id=str(AddrBook.chain_ids_by_name["gnosis"])
)
BAL_token_gnosis = SafeContract("tokens/BAL", get_abi("ERC20"))
for address, amount in send_to_gnosis.items():
    BAL_token_gnosis.transfer(address, amount)
gnosis_builder.output_payload("ZenAirdropGnosis.json")
